import{_ as a}from"./chunks/QABox.26c056e2.js";import"./chunks/index.8a7f7853.js";import{I as r}from"./chunks/index.682258f9.js";import{_ as i,K as u,o as c,c as d,O as t,w as s,X as p,k as n,a as l}from"./chunks/framework.683b9a23.js";import"./chunks/index.a5ffbb23.js";import"./chunks/index.5a8a3196.js";import"./chunks/use-index.0b51a5ca.js";import"./chunks/index.6ced3d4b.js";import"./chunks/ResizeObserver.es.0f9f8adb.js";import"./chunks/index.92521e15.js";import"./chunks/index.ec731783.js";import"./chunks/index.9c7b8b8b.js";import"./chunks/index.452e785d.js";import"./chunks/index.560a6753.js";import"./chunks/index.2a01ce05.js";import"./chunks/index.dc70425f.js";const qn=JSON.parse('{"title":"游民星空专访","description":"","frontmatter":{"title":"游民星空专访","author":"风城之心"},"headers":[],"relativePath":"games/pc/13-轩辕剑柒/zhuanfang/youmin.md","filePath":"games/pc/13-轩辕剑柒/zhuanfang/youmin.md","lastUpdated":1699240770000}'),h={name:"games/pc/13-轩辕剑柒/zhuanfang/youmin.md"},m=p('<h2 id="◆遊民星空專訪-我們和《軒轅劍7》工作室聊了聊-還有一張獨家截圖" tabindex="-1">◆遊民星空專訪 我們和《軒轅劍7》工作室聊了聊：還有一張獨家截圖 <a class="header-anchor" href="#◆遊民星空專訪-我們和《軒轅劍7》工作室聊了聊-還有一張獨家截圖" aria-label="Permalink to &quot;◆遊民星空專訪  我們和《軒轅劍7》工作室聊了聊：還有一張獨家截圖&quot;">​</a></h2><h3 id="《軒轅劍7》domo工作室專訪-追求更好的畫面和沉浸感" tabindex="-1">《軒轅劍7》DOMO工作室專訪：追求更好的畫面和沉浸感 <a class="header-anchor" href="#《軒轅劍7》domo工作室專訪-追求更好的畫面和沉浸感" aria-label="Permalink to &quot;《軒轅劍7》DOMO工作室專訪：追求更好的畫面和沉浸感&quot;">​</a></h3><p>本文來自遊民星空，發表時間2020-03-12 12:08<br></p><p>本文作者：不倒翁蜀黍<br></p><p><span class="leading-10">《軒轅劍》系列在中國武俠遊戲當中，可能是把歷史文化底蘊和天馬行空的想像力結合得最好的一個。在這個世界裡，諸葛亮依靠神秘的特種部隊取得了一次又一次戰術勝利；南北朝時期秦晉兩國淝水之戰的結果竟然跟一千年前春秋時代的“一件小事”息息相關；墨家依靠機關術甚至直接創造出了彷彿從科幻小說裡蹦出來的機甲戰士……而如今，距離上一部《軒轅劍》正統單機作品《穹之扉》發售已經過了整整5年，續作《軒轅劍7》的開發工作也漸漸接近尾聲。很榮幸，遊民星空受到邀請對製作組DOMO工作室進行了線上採訪，並對玩家和粉絲最關心的問題進行了提問。</span></p>',5),w=n("br",null,null,-1),b=n("br",null,null,-1),g=n("span",null,"目前已經進入調整與優化期，所有項目都在努力的催生中，具體發售的時間目前還要和發行商再協商，暫時無法公佈。",-1),f=n("span",null,"就遊戲視覺而言，對比五年前的穹之扉，我們在人物與建築結構的比例拿捏、人物質感、表情動作等有更多的著墨。",-1),q=n("br",null,null,-1),O=n("span",null,"引擎與渲染方面，Unreal 4 引擎預設就是架構在PBR (Physically Based Rendering)的基礎之上，而PBR簡而言之就是基於物理特性的渲染方式，讓光線投射在物體後能呈現出更多細節。相較過去僅有Mormal 、Specular、AO的材質結構來說，新導入的Roughness、Metallic能夠呈現更細膩且真實的光線效果。",-1),P=n("br",null,null,-1),x=n("span",null,"更好的畫面表現我想是每個遊戲人都樂於追求的目標，這應該不僅止於《軒轅劍7》。畢竟單機遊戲是一個綜合性的娛樂(以及藝術)產品，從劇本、畫面、聽覺、手感直至成就感，最終獲得滿足。",-1),k=n("br",null,null,-1),y=n("span",null,"而畫質這塊，則是最能帶給玩家沉浸感的一個環節。且透過更高畫質的呈現，對於營造軒轅劍的世界觀也會有很大的幫助。",-1),B=n("br",null,null,-1),D=n("span",null,"我們會努力儘量讓所有平臺都同步上市，不過這並非單純開發層面的問題，所以一切都還在努力當中。而售價需要與發行商等相關人員討論，暫時還沒有一個定案。",-1),M=n("br",null,null,-1),S=n("span",null,"我認為不少故事都是建立在這樣的結構之中，試想無論是愛情、冒險、商場、官場等等故事，很可能是某人過去真實發生的生活(歷史)改編的，故事之所以精彩很大部分取決於足夠的真實共鳴，這共鳴並非是真實度有多高，而是能感受到的真實有多深，要達到這樣的程度，用大家熟知的古今中外的歷史確實是一條快捷方式，不過還是得小心翼翼、戰戰兢兢地去寫才不會弄巧成拙。",-1),T=n("br",null,null,-1),N=n("span",null,"軒七一開始有好幾個版本，從漢到明朝都有，但過程中有許多現實的狀況；最終只好選擇目前團隊最合適發揮的年代，這個時期裡有很多驚奇有趣的事件，都是團隊可以好好琢磨的內容。",-1),R=n("br",null,null,-1),V=n("span",null,"由於四代之中姬良已經算是“封印”了機關術與墨家，因此本作從白輿老人一脈琢磨要如何呈現出不一樣的內容，敬請玩家拭目以待。",-1),A=n("br",null,null,-1),C=n("span",null,"本作除了過去軒轅劍收妖、煉妖、煉化的經典系統之外，跟戰鬥息息相關的天賦系統將會帶給玩家一個全新的體驗，另外由於本作與天書關係甚大，故也會有一個與天書息息相關的系統。再請繼續關注軒七的後續消息。",-1),I=n("br",null,null,-1),$=n("span",null,"本作戰鬥將採即時制。個人覺得同為遊戲機制實在沒有孰好孰壞的問題，而不同機制也存在著各自的喜惡；這並沒有優劣對錯好壞之別，團隊不管做那種機制，都應該努力做到有趣好玩，這點比較重要。",-1),j=n("br",null,null,-1),v=n("span",null,"故事中的角色給人印象深刻的，無論是令人厭惡的、喜愛的、感動的、憤恨的…等那種角色，最重要的是能讓能感受到人物的血性與共鳴點。努力塑造出這樣的角色，應都能讓人感覺深刻。",-1),z=n("br",null,null,-1),E=n("span",null,"除了原本的軒轅劍玩家之外，希望本次的革新也能讓動作RPG或非軒轅劍的玩家喜歡，而這些廣大的玩家肯定有許多不同的品味，我們也不太可能滿足所有玩家的不同喜好；只求盡力表現出自己能力所及的最好，希望玩家能感受到產品的用心就足以。",-1),U=n("br",null,null,-1),X=n("span",null,"其實不管哪個產品的團隊，一定是期望產品是叫好又叫座，這樣才有辦法繼續推出更多好的作品回饋玩家，DOMO當然也不會例外。",-1),G=n("br",null,null,-1),J=n("span",null,"團隊專注開發遊戲，對於維護品牌價值上應該就是把遊戲做好，而其他層面的事情，恐怕是團隊不擅長的項目，也不便發表太多言論。",-1),K=n("br",null,null,-1),Q=n("span",null,"開發過程的趣事實在是太多了，還真不知道要提那一段，但因為我們引用不少新技術的關係，同步做了不少開發歷程的紀錄，屆時有機會可以分享給各位玩家觀看。",-1),F=n("br",null,null,-1),H=n("p",null,[l("專訪原始連結："),n("a",{href:"https://www.gamersky.com/news/202003/1270834.shtml",target:"_blank",rel:"noreferrer"},"https://www.gamersky.com/news/202003/1270834.shtml")],-1);function L(W,Y,Z,nn,sn,tn){const e=r,o=a,_=u("center");return c(),d("div",null,[m,t(e,{src:"./img/md001.jpg",width:"100%"}),w,b,t(o,null,{question:s(()=>[l("本作立項最早在2017年就已經公佈，去年曾宣佈預計會在2020年夏季發售。請問遊戲開發順利嗎？我們是否能在幾個月後就玩上《軒轅劍7》？")]),answer:s(()=>[g]),_:1}),t(o,null,{question:s(()=>[l("《軒轅劍7》採用虛幻4引擎打造，能具體談談會在哪些方面強化遊戲畫面的真實度嗎？您認為為什麼《軒轅劍》需要追求更好的畫質表現？")]),answer:s(()=>[f,q,O,P,x,k,y,B,t(e,{src:"./img/md002.jpg",alt:"▲遊民獨家實機截圖",width:"100%"}),t(_,null,{default:s(()=>[l("▲遊民獨家實機截圖")]),_:1})]),_:1}),t(o,null,{question:s(()=>[l("除了PC之外，《軒轅劍7》還會登上Xbox One和PS4，請問所有平臺的版本會同步發售嗎？各個平臺的版本預計售價分別是多少？")]),answer:s(()=>[D,M]),_:1}),t(o,null,{question:s(()=>[l("《軒轅劍》系列的故事一直以來都相當注重歷史文化底蘊，巧妙地融合真實的歷史和虛構的神話。您認為這種特色的優勢是什麼？")]),answer:s(()=>[S,T]),_:1}),t(o,null,{question:s(()=>[l("本作的時代背景設定在西漢末年，為什麼選擇“王莽篡漢”這段特殊的歷史時期？")]),answer:s(()=>[N,R]),_:1}),t(o,null,{question:s(()=>[l("繼四代作品之後，《軒轅劍7》將再一次正面展現“墨家”和“機關術”的風采。請問這一次將會帶來哪些新東西？")]),answer:s(()=>[V,A]),_:1}),t(o,null,{question:s(()=>[l("在遊戲系統方面，本作最重要的革新將會是在哪個方面？")]),answer:s(()=>[C,I]),_:1}),t(o,null,{question:s(()=>[l("戰鬥方式將會是即時制還是回合制？請問您怎麼看待玩家社群中關於即時制與回合制孰優孰劣的爭辯？")]),answer:s(()=>[$,j]),_:1}),t(o,null,{question:s(()=>[l("《軒轅劍》系列曾創造出了許多受歡迎的經典角色。你認為遊戲中的角色給玩家留下深刻印象的關鍵是什麼？")]),answer:s(()=>[v,z]),_:1}),t(o,null,{question:s(()=>[l("《軒轅劍7》的目標受眾預計主要會是哪些玩家？不同群體和不同年齡段的玩家之間可能在審美偏好上存在較大偏差，男性和女性、系列粉絲和新玩家、1980後跟2000後希望在遊戲裡玩到的元素可能完全不一樣。請問《軒轅劍7》如何應對這些差異？ ")]),answer:s(()=>[E,U]),_:1}),t(o,null,{question:s(()=>[l("《軒轅劍7》希望達到一個怎樣的市場效果？")]),answer:s(()=>[X,G,t(e,{src:"./img/md003.jpg",width:"100%"})]),_:1}),t(o,null,{question:s(()=>[l("如今《軒轅劍》已經成為了中國遊戲市場上最有價值的IP之一，除了正統遊戲之外也衍生出了許多手遊和影視劇。在維護《軒轅劍》品牌價值方面，你認為最重要的因素是什麼？ ")]),answer:s(()=>[J,K]),_:1}),t(o,null,{question:s(()=>[l("可以分享一些DOMO工作室在開發《軒轅劍7》的過程中的趣事嗎？ ")]),answer:s(()=>[Q,F]),_:1}),H])}const On=i(h,[["render",L]]);export{qn as __pageData,On as default};
